# Helper function to generate samples for all case types
def generate_all_cases():
    """모든 사건 유형에 대한 샘플 생성"""

    base_dir = Path(__file__).parent

    cases = [
        ("횡령배임_형사사건_2024", generate_embezzlement_case, 2001),
        ("지식재산권_침해소송", generate_ip_litigation_case, 2002),
        ("노동부_근로감독_대응", generate_labor_inspection_case, 2003),
        # Add more case generators here...
    ]

    total_generated = 0

    for folder_name, generator_func, project_id in cases:
        output_dir = base_dir / folder_name / "ocr_summaries"
        output_dir.mkdir(parents=True, exist_ok=True)

        print(f"\n{'='*60}")
        print(f"Generating samples for: {folder_name}")
        print(f"{'='*60}")

        samples = generator_func(output_dir, project_id)
        base_date = datetime(2024, 1, 1, 9, 0, 0)

        for i, sample in enumerate(samples, start=1):
            created_at = base_date + timedelta(days=i-1, hours=i % 24)

            summary_data = {
                "summary_id": str(uuid.uuid4()),
                "project_id": project_id,
                "file_id": sample["file_id"],
                "summary_text": sample["summary"],
                "created_at": created_at.isoformat() + "+00:00"
            }

            output_file = output_dir / f"{sample['filename']}.json"
            with open(output_file, 'w', encoding='utf-8') as f:
                json.dump(summary_data, f, ensure_ascii=False, indent=2)

            print(f"✅ Generated: {output_file.name}")

        print(f"\n📊 {folder_name}: {len(samples)} files generated")
        total_generated += len(samples)

    print(f"\n{'='*60}")
    print(f"🎉 Total samples generated: {total_generated}")
    print(f"{'='*60}")


if __name__ == "__main__":
    generate_all_cases()
